// JSON Minify Tests framework.test('JSON prettify basic', () => { const input = '{"name":"John","age":30}'; const parsed = JSON.parse(input); const result = JSON.stringify(parsed, null, 2); framework.assertTrue(result.includes(' "name": "John"')); }); framework.test('JSON minify basic', () => { const input = '{
 "name": "John",
 "age": 30
}'; const parsed = JSON.parse(input); const result = JSON.stringify(parsed); framework.assertEqual(result, '{"name":"John","age":30}'); });

// Text Statistics - Converted from IT-Tools class TextStatistics { constructor() { this.textInput = document.getElementById('textInput'); this.statsContainer = document.getElementById('stats'); this.init(); } init() { this.textInput.addEventListener('input', () => this.calculateStats()); this.calculateStats(); } calculateStats() { const text = this.textInput.value; const stats = this.getTextStats(text); this.displayStats(stats); } getTextStats(text) { const characters = text.length; const charactersNoSpaces = text.replace(/\s/g, '').length; const words = text.trim() ? text.trim().split(/\s+/).length : 0; const sentences = text.trim() ? text.split(/[.!?]+/).filter(s => s.trim().length > 0).length : 0; const paragraphs = text.trim() ? text.split(/
\s*
/).filter(p => p.trim().length > 0).length : 0; const lines = text.split('
').length; // Approximate token count (rough estimation: ~4 characters per token) const estimatedTokens = Math.ceil(charactersNoSpaces / 4); // Reading time (average 200 words per minute) const readingTimeMinutes = Math.ceil(words / 200); return { characters, charactersNoSpaces, words, sentences, paragraphs, lines, estimatedTokens, readingTimeMinutes }; } displayStats(stats) { this.statsContainer.innerHTML = ''; const statItems = [ { label: 'Characters', value: stats.characters }, { label: 'Characters (no spaces)', value: stats.charactersNoSpaces }, { label: 'Words', value: stats.words }, { label: 'Sentences', value: stats.sentences }, { label: 'Paragraphs', value: stats.paragraphs }, { label: 'Lines', value: stats.lines }, { label: 'Estimated Tokens (LLM)', value: stats.estimatedTokens } ]; statItems.forEach(item => { const statCard = document.createElement('div'); statCard.className = 'stat-card'; statCard.innerHTML = ` ${item.label} ${item.value.toLocaleString()} `; this.statsContainer.appendChild(statCard); }); // Reading time card (special styling) const readingCard = document.createElement('div'); readingCard.className = 'stat-card reading-time'; readingCard.innerHTML = ` Estimated Reading Time ${stats.readingTimeMinutes} min `; this.statsContainer.appendChild(readingCard); } } // Initialize when DOM is loaded document.addEventListener('DOMContentLoaded', () => { new TextStatistics(); });

// SQL Prettify - Converted from IT-Tools class SqlPrettify { constructor() { this.sqlInput = document.getElementById('sqlInput'); this.result = document.getElementById('result'); this.keywordCaseSelect = document.getElementById('keywordCase'); this.indentStyleSelect = document.getElementById('indentStyle'); this.init(); } init() { this.sqlInput.addEventListener('input', () => this.autoFormat()); this.keywordCaseSelect.addEventListener('change', () => this.autoFormat()); this.indentStyleSelect.addEventListener('change', () => this.autoFormat()); this.autoFormat(); } autoFormat() { const sql = this.sqlInput.value; if (sql) { const keywordCase = this.keywordCaseSelect.value; const indentStyle = this.indentStyleSelect.value; this.result.textContent = this.formatSql(sql, keywordCase, indentStyle); } else { this.result.textContent = ''; } } formatSql(sql, keywordCase = 'upper', indentStyle = 'standard') { // Remove extra whitespace and normalize sql = sql.replace(/\s+/g, ' ').trim(); // Keywords that should be on new lines const keywords = [ 'SELECT', 'FROM', 'WHERE', 'JOIN', 'INNER JOIN', 'LEFT JOIN', 'RIGHT JOIN', 'FULL JOIN', 'GROUP BY', 'ORDER BY', 'HAVING', 'UNION', 'INSERT', 'UPDATE', 'DELETE', 'CREATE', 'ALTER', 'DROP', 'AND', 'OR' ]; // Add line breaks before major keywords keywords.forEach(keyword => { const regex = new RegExp(`\b${keyword}\b`, 'gi'); const replacement = indentStyle === 'tabular-right' && ['FROM', 'WHERE', 'GROUP BY', 'ORDER BY'].includes(keyword.toUpperCase()) ? `
 ${keyword}` : `
${keyword}`; sql = sql.replace(regex, replacement); }); // Handle SELECT fields based on indent style if (indentStyle === 'tabular-left') { sql = sql.replace(/SELECT\s+(.+?)\s+FROM/gi, (match, fields) => { const fieldArray = fields.split(',').map(field => field.trim()); const firstField = fieldArray[0] + ','; const restFields = fieldArray.slice(1).map((field, index) => { const isLast = index === fieldArray.length - 2; return ' ' + field + (isLast ? '' : ','); }).join('
'); return `SELECT ${firstField}${restFields ? '
' + restFields : ''}
FROM `; }); } else if (indentStyle === 'tabular-right') { sql = sql.replace(/SELECT\s+(.+?)\s+FROM/gi, (match, fields) => { const fieldArray = fields.split(',').map(field => field.trim()); const firstField = fieldArray[0] + ', '; const restFields = fieldArray.slice(1).map((field, index) => { const isLast = index === fieldArray.length - 2; return ' ' + field + (isLast ? '' : ','); }).join('
'); return `SELECT ${firstField}${restFields ? '
' + restFields : ''}
 FROM `; }); } else { sql = sql.replace(/SELECT\s+(.+?)\s+FROM/gi, (match, fields) => { const fieldList = fields.split(',').map(field => ' ' + field.trim()).join(',
'); return `SELECT
${fieldList}
 FROM`; }); } // Apply keyword case if (keywordCase !== 'preserve') { keywords.forEach(keyword => { const regex = new RegExp(`\b${keyword}\b`, 'gi'); const replacement = keywordCase === 'upper' ? keyword.toUpperCase() : keyword.toLowerCase(); sql = sql.replace(regex, replacement); }); } // Indent subqueries and conditions const lines = sql.split('
'); let indentLevel = 0; const formatted = lines.map(line => { const originalLine = line; line = line.trim(); if (!line) return ''; // Skip indenting if already formatted for tabular if (indentStyle !== 'standard' && (originalLine.startsWith('SELECT') || originalLine.startsWith(' FROM') || originalLine.includes(', '))) { return originalLine; } // Decrease indent for closing keywords if (line.match(/^\)/)) { indentLevel = Math.max(0, indentLevel - 1); } const indent = ' '.repeat(indentLevel); // Increase indent for opening keywords if (line.match(/\($/)) { indentLevel++; } return indent + line; }).join('
'); return formatted.trim(); } minifySql(sql) { return sql.replace(/\s+/g, ' ').trim(); } } // Global functions for buttons function formatSql() { const sqlInput = document.getElementById('sqlInput'); const result = document.getElementById('result'); const keywordCaseSelect = document.getElementById('keywordCase'); const indentStyleSelect = document.getElementById('indentStyle'); const sql = sqlInput.value; if (sql) { const prettifier = new SqlPrettify(); const keywordCase = keywordCaseSelect.value; const indentStyle = indentStyleSelect.value; result.textContent = prettifier.formatSql(sql, keywordCase, indentStyle); } } function minifySql() { const sqlInput = document.getElementById('sqlInput'); const result = document.getElementById('result'); const sql = sqlInput.value; if (sql) { const prettifier = new SqlPrettify(); result.textContent = prettifier.minifySql(sql); } } function copyResult() { const result = document.getElementById('result'); const text = result.textContent; if (navigator.clipboard) { navigator.clipboard.writeText(text).then(() => { showCopySuccess(); }).catch(err => { fallbackCopyTextToClipboard(text); }); } else { fallbackCopyTextToClipboard(text); } } function fallbackCopyTextToClipboard(text) { const textArea = document.createElement("textarea"); textArea.value = text; textArea.style.top = "0"; textArea.style.left = "0"; textArea.style.position = "fixed"; document.body.appendChild(textArea); textArea.focus(); textArea.select(); try { document.execCommand('copy'); showCopySuccess(); } catch (err) { console.error('Fallback: Oops, unable to copy', err); } document.body.removeChild(textArea); } function showCopySuccess() { const btn = document.querySelector('.copy-btn'); const originalText = btn.textContent; btn.textContent = 'Copied!'; btn.style.background = '#28a745'; setTimeout(() => { btn.textContent = originalText; btn.style.background = '#007acc'; }, 2000); } // Initialize when DOM is loaded document.addEventListener('DOMContentLoaded', () => { new SqlPrettify(); });

// SQL Prettify Tests framework.test('SQL formatting basic', () => { const sql = 'SELECT name, age FROM users WHERE active = 1'; framework.assertTrue(sql.includes('SELECT')); framework.assertTrue(sql.includes('FROM')); framework.assertTrue(sql.includes('WHERE')); }); framework.test('SQL keyword case upper', () => { const sql = 'select name from users'; const result = sql.replace(/ select /gi, 'SELECT').replace(/ from /gi, 'FROM'); framework.assertTrue(result.includes('SELECT')); framework.assertTrue(result.includes('FROM')); }); framework.test('SQL keyword case lower', () => { const sql = 'SELECT name FROM users'; const result = sql.replace(/ SELECT /gi, 'select').replace(/ FROM /gi, 'from'); framework.assertTrue(result.includes('select')); framework.assertTrue(result.includes('from')); }); framework.test('SQL tabular left formatting', () => { const fields = 'name, age, email'; const fieldArray = fields.split(',').map(field => field.trim()); const firstField = fieldArray[0]; const restFields = fieldArray.slice(1).map(field => ' , ' + field).join('
'); const result = `SELECT ${firstField}${restFields ? '
' + restFields : ''}
 FROM table`; framework.assertTrue(result.includes('SELECT name,')); framework.assertTrue(result.includes(' age,')); framework.assertTrue(result.includes(' email')); framework.assertTrue(result.includes('FROM table')); }); framework.test('SQL tabular right formatting', () => { const fields = 'name, age, email'; const fieldArray = fields.split(',').map(field => field.trim()); const maxLength = Math.max(...fieldArray.map(f => f.length)); const alignedFields = fieldArray.map((field, index) => { if (index === 0) return field.padEnd(maxLength); return field.padEnd(maxLength) + ' ,'; }).join('
 '); const result = `SELECT ${alignedFields}
 FROM`; framework.assertTrue(result.includes('SELECT name, ')); framework.assertTrue(result.includes(' age,')); framework.assertTrue(result.includes(' email')); framework.assertTrue(result.includes(' FROM table')); });
